package java_package;

public class Evens_from_20_to_40 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int b=20;b<=40;b++){			
			if(b%2 == 0) {
				System.out.println(b+" :This number is even: ");
			}
			else
			{
				System.out.println(b+" This number is odd: ");
			}
		}
	}
}